import "./App.css";
import Button from "./components/Button";
import Goals from "./components/Goals";
import Header from "./components/Header";
import Searchbar from "./components/Searchbar";
import { useEffect, useState } from "react";

function App() {
  const [goals, setGoals] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  // fetch the goals data
  useEffect(() => {
    const getGoals = async () => {
      const goalsFromServer = await fetchGoals();

      setGoals(goalsFromServer);
    };
    getGoals();
  }, []);

  const fetchGoals = async () => {
    const res = await fetch("http://localhost:5000/goals");
    const data = await res.json();

    return data;
  };

  // search the goals in search bar
  const searchGoal = async (e) => {
    setSearchTerm(e.target.value);

    if (searchTerm !== "") {
      setGoals(
        goals.filter((goalSearch) =>
          goalSearch.goal.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    } else {
      setSearchTerm("");
      const res = await fetchGoals();
      const data = await res.json();

      setGoals(data);
    }
  };

  return (
    <div className="App">
      <Header />
      <div className="main-section">
        <Searchbar
          onChange={(e) => {
            searchGoal(e);
          }}
        />
        <Button text="Add Goal" />
      </div>
      {goals > 0 ? "No Goals to show!" : <Goals goals={goals} />}
    </div>
  );
}

export default App;
